sites.css:
sr-rd-content .figure {
    all: unset;
}
sr-rd-content .head, sr-rd-content .sub-head {
    border-bottom: none;
}

sr-rd-content section.abstract, sr-rd-content section.abstract * {
    all: unset!important;
}
sr-rd-content .citation div {
    all: unset;
}

sr-rd-content .citation.mixed-citation {
    all: unset;
}

sr-rd-content .dropBlock__body {
    display: none;
}
sr-rd-content .refs ol:not([class^=rlist]):not([class~=rlist])>li.ref:before {
    display: none;
}

sr-rd-content .article-section__toggle:after {
    display: none!important;
}
sr-rd-content a.article-section__toggle {
    text-decoration: none!important;
}
sr-rd-content .article-section--js {
    border: none!important;
}

sr-rd-content .c-article-box__container {
    height: initial!important;
}
sr-rd-content .c-article-box__controls {
    display: none;
}

sr-rd-content .FigureDesc {
    background: #eee;
    padding: 20px;
    height: 100%;
}

sr-rd-content .FigureDesc .sr-rd-content-center-small {
    all: unset!important;
}

sr-rd-content .FigureDesc img {
    float: left;
    margin-top: 40px;
    margin-right: 30px;
}

sr-rd-content .FigureDesc p {
    color: #3e3d40;
    font-family: Georgia,"Times New Roman",Times,serif;
    font-size: 18px;
    line-height: 28px;
    font-weight: 300;
    margin: 0 0 10px;
}
